var isPalindrome = function(x) {
    if(x===0){
        return true;
    }
    if(x<0||x%10==0){
        return false;
    }

    let cur=0;
    let num=x;
    while(num>=10){
        cur=cur*10+num%10;
        num=Math.floor(num/10);
    }

    return (cur*10+num)==x;
};

