const App = () => {
  const [count, setCount] = useState(0);
  useEffect(() => {
    const timerId = setInterval(() => {
      setCount(count + 1);
    }, 1000);
    return () => {
      // \u7528\u6765\u6E05\u7406\u526F\u4F5C\u7528\u7684\u4E8B\u60C5
      clearInterval(timerId);
    };
  }, [count]);
  return <div>{count}</div>;
};

export default App;
