// \u521B\u5EFAContext\u5BF9\u8C61
const Context = createContext();

function Foo() {
  return (
    <div>
      Foo <Bar />
    </div>
  );
}

function Bar() {
  // \u5E95\u5C42\u7EC4\u4EF6\u901A\u8FC7useContext\u51FD\u6570\u83B7\u53D6\u6570\u636E
  const name = useContext(Context);
  return <div>Bar {name}</div>;
}

function App() {
  return (
    // \u9876\u5C42\u7EC4\u4EF6\u901A\u8FC7Provider \u63D0\u4F9B\u6570\u636E
    <Context.Provider value={'this is name'}>
      <div>
        <Foo />
      </div>
    </Context.Provider>
  );
}

export default App;
