// \u51C6\u5907\u4FE9\u4E2A\u8DEF\u7531\u7EC4\u4EF6

const Home = () => <div>this is home</div>;
const About = () => <div>this is about</div>;

function App() {
  return (
    <div className="App">
      {/* \u6309\u7167\u89C4\u5219\u914D\u7F6E\u8DEF\u7531 */}
      <BrowserRouter>
        <Link to="/">\u9996\u9875</Link>
        <Link to="/about">\u5173\u4E8E</Link>
        //\u8DEF\u7531\u51FA\u53E3
        <Routes>
          <Route path="/" element={<Home />}></Route>
          <Route path="/about" element={<About />}></Route>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
